questions.sparqlQuery:
PREFIX wd: <http://www.wikidata.org/entity/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT ?capitalLabel (SAMPLE(?landmarkLabel) AS ?landmarkLabel) (SAMPLE(?image) AS ?image) WHERE {
  # Buscar capitales del mundo
  ?capital wdt:P31 wd:Q5119.

  # Elementos representativos en la capital
  {
    ?landmark wdt:P131 ?capital.  # Ubicación directa en la capital
  } UNION {
    ?landmark wdt:P276 ?capital.  # Ubicado en la capital
  } UNION {
    ?region wdt:P150 ?capital.
    ?landmark wdt:P131 ?region.  # Ubicado en la región de la capital
  }

  # Asegurar que el landmark tiene nombre
  OPTIONAL {
    ?landmark rdfs:label ?landmarkLabel.
    FILTER(LANG(?landmarkLabel) = "es")
  }
  OPTIONAL {
    ?landmark rdfs:label ?landmarkLabel_en.
    FILTER(LANG(?landmarkLabel_en) = "en")
  }

  # Si no hay nombre en español, usar inglés
  BIND(COALESCE(?landmarkLabel, ?landmarkLabel_en) AS ?landmarkLabel)

  # Obtener una imagen (si existe)
  OPTIONAL { ?landmark wdt:P18 ?image }

  SERVICE wikibase:label { bd:serviceParam wikibase:language "es,en" }
}
GROUP BY ?capitalLabel
LIMIT 50
